import React, { useState, useEffect, useRef } from 'react';
import _ from 'lodash';
const APPLERADIUS = 10

const randomXY = (snake, gameContainerWidth, gameContainerHeight) => {

  const randX = _.random(gameContainerWidth / 25) * 25
  const randY = _.random(gameContainerHeight / 25) * 25
  return _.includes(snake.segmentLocations, [randX, randY])? randomXY(snake, gameContainerWidth, gameContainerHeight):[randX,randY]

}

class GameContainer {

  constructor(width, height){
    this.width = width
    this.height = height
    this.wallCollision = false
  }

  update(snake, apple){
    // Update the snake, did it eat the apple or not
    (snake.segmentLocations[0][0] === apple.x && snake.segmentLocations[0][1] === apple.y)? snake.update(true): snake.update(false)
    // Update the apple, was it eaten or not
    if (snake.segmentLocations[0][0] === apple.x && snake.segmentLocations[0][1] === apple.y){
      // non random regeneration of apple for now
       const [x, y] = randomXY(snake, this.width, this.height)
       apple.x = x
       apple.y = y
    }
    // check of snake collided with wall
    this.wallCollision = (snake.segmentLocations[0][0] >= this.width - 25 || snake.segmentLocations[0][0] <= 0 || snake.segmentLocations[0][1] >= this.height -25 || snake.segmentLocations[0][1] <= 0)?  true: false
  }

}

class Snake {

  constructor()
  {
    this.segmentLocations = [[50,50], [25,50], [0,50]]
    this.head = this.segmentLocations[0]
    this.direction = "right"
    this.crashed = false
  }

  checkIfCrashed(){
    this.segmentLocations.slice(1,).forEach((segment) =>{
    if (_.isEqual(this.head, segment)){
       this.crashed = true
    }})
}

  update(addSegment){
    if (!this.crashed){
      let currentHead
      if (this.direction === "right"){
        currentHead = [this.head[0] + 25, this.head[1]]
      }
      else if (this.direction === "left"){
        currentHead = [this.head[0] - 25, this.head[1]]
      }
      else if (this.direction === "up"){
        currentHead = [this.head[0], this.head[1] - 25]
      }
      else if (this.direction === "down"){
        currentHead = [this.head[0], this.head[1] + 25]
      }
      this.head = currentHead
      this.segmentLocations = [currentHead].concat(this.segmentLocations)

      if (!addSegment){
        this.segmentLocations.pop()
      }
      this.checkIfCrashed()
  }
}

  changeDirection(direction){
    this.direction = direction
  }

}

class Apple {

  constructor(location)
  {
    this.x = location.x
    this.y = location.y
  }
}


const Game = () => {

  const gameContainer = new GameContainer(400, 400)
  const snake = new Snake()
  const apple = new Apple({x:100, y:100})

  const gameLogic = (gameContainer, snake, apple, ctx) => {

    if (!gameContainer.wallCollision && !snake.crashed){
        // Draw Game Contaier
        gameContainer.update(snake, apple)
        ctx.clearRect(0,0, gameContainer.width, gameContainer.height)
        ctx.fillStyle = 'black';
        ctx.fillRect(0, 0, gameContainer.x, gameContainer.y)
        // Draw Snake
        ctx.fillStyle = 'green';
        for (let index = 0; index < snake.segmentLocations.length; index++) {
          const segment = snake.segmentLocations[index]
          ctx.fillRect(segment[0], segment[1], 25, 25);
        }
        // Draw Apple
        ctx.fillStyle = 'red';
        ctx.fillRect(apple.x, apple.y, 25, 25)
  }

}

  const canvasRef = useRef(null)

  const controller = (event) => {

    switch (event.code) {
      case "ArrowRight":
        snake.changeDirection("right")
        break
      case "ArrowLeft":
        snake.changeDirection("left")
        break
      case "ArrowUp":
        snake.changeDirection("up")
        break
      case "ArrowDown":
        snake.changeDirection("down")
        break
      default:
        break;
    }
    switch (event.keyCode ) {
      case 32:
        snake.pauseSnake()
        break
      case 13:
        snake.go()
        break
      default:
        break;
    }

  }



  useEffect(() => {


      const canvas = canvasRef.current
      const ctx = canvas.getContext("2d");

      let gameState = setInterval(() => {gameLogic(gameContainer, snake, apple, ctx);}, 150)
      window.addEventListener('keydown', controller);

      return () => {
      clearInterval(gameState)
      window.removeEventListener('keydown', controller);
      }
  })


  return(
    <div>
        <h1 style ={{fontFamily: "'Press Start 2P', cursive", textAlign: "center"}}> Snake</h1>
        <canvas
          style={{
          background: "#262626",
          display: "block",
          margin: "0 auto" }}
          id="myCanvas"
          width="400"
          height="400"
          ref={canvasRef}></canvas>
    </div>
  )
  }

function App() {

  return(
    <>
    <Game/>
    </>
  )


}

export default App;
