import React, { useState, useEffect, useRef } from 'react';
import EventListener, {withOptions} from 'react-event-listener';

class Snake {
  constructor(x, y, direction) {
    this.x = x
    this.y = y
    this.direction = direction
  }

  changeDirection(direction){
    this.direction = direction
  }

  updatePostion(){
    if (this.direction == 'up'){
      this.y += 20
    }
    else if (this.direction == 'down'){
      this.y -= 20
    }
    else if (this.direction == 'left'){
      this.x -= 20
    }
    else if (this.direction == 'right'){
      this.x += 20
    }
  }

  get x(){
    return this.x
  }

  get y(){
    return this.y
  }
}

const snake = new Snake(100, 100, 'right')
const Position = ({stateX, stateY}) => {

  return(
    <div style={{marginLeft: "100px"}}> {stateX} {stateY} </div>
  )
}

const Game = React.memo(({setStateX, setStateY}) => {

  let dx = 0
  let dy = 0

  let appleExists = false

  const squares = {}
  for (let i = 0; i < 16; i++){
    squares[i * 20] = {}
    for (let j = 0; j < 24; j++){
      squares[i * 20][j * 20] = false
    }
  }


  const genRandomXY = () => {
    const y = Math.floor(Math.random() * 15)
    const x = Math.floor(Math.random() * 23)
    return [x, y]
  }

  let cW
  let cH

  useEffect(() => {

      let coords = genRandomXY()

      let x = coords[0] * 20
      let y = coords[1] * 20

      squares[x][y] = 'apple'

      const canvas = cRef.current
      const ctx = canvas.getContext("2d");
      cW = canvas.width/2
      cH = canvas.height/2
      squares[cW][cH] = true

      setStateX(cW)
      setStateY(cH)
      setInterval(() => {draw(ctx, canvas); snake.updatePostion()}, 10)
      window.addEventListener('keydown', logKey, cW, cH);
      return () => {
      window.removeEventListener('keydown', logKey, cW, cH);
  }
  })

  const logKey = (event) => {

    if (event.code === "ArrowRight"){
      snake.changeDirection("right")
    }
    else if (event.code === "ArrowLeft"){
      snake.changeDirection("left")
    }
    else if (event.code === "ArrowUp" ){
      snake.changeDirection("up")
    }
    else if (event.code === "ArrowDown"){
      snake.changeDirection("down")
    }

  }

  function draw(ctx, canvas) {


    const squareSide = 20
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.beginPath()
    ctx.rect(snake.x, snake.y, squareSide, squareSide);
    ctx.fillStyle = "#388E3C";
    ctx.fill();
    ctx.closePath();

}

  function drawApple(ctx, canvas, x, y) {

      const appleRadius = 10
      ctx.beginPath()
      ctx.arc(x + 10, y + 10, appleRadius, 0, Math.PI*2);
      ctx.fillStyle = "#D32F2F";
      ctx.fill();
      ctx.closePath();

    }




  const cRef = useRef(null)
  return(
    <div>
        <canvas  style={{ background: "#eee", display: "block", margin: "0 auto" }} id="myCanvas" width="480" height="320" ref={cRef}></canvas>
    </div>
  )
})


function App() {

  const [stateX, setStateX] = useState(240)
  const [stateY, setStateY] = useState(160)

  return (
    <div>
        <Game  setStateX={setStateX} setStateY={setStateY}/>
        <Position stateX={stateX} stateY={stateY}/>
    </div>
  );
}

export default App;
