import EventListener, {withOptions} from 'react-event-listener';



function App() {

  useEffect(() => {

      const canvas = cRef.current
      const ctx = canvas.getContext("2d");
      setInterval(() => {draw(ctx, canvas);}, 10)
      window.addEventListener('keydown', logKey);
      return () => {
      window.removeEventListener('keydown', logKey);
  }
  })

  function draw(ctx, canvas) {

  const squareSide = 20
  ctx.beginPath()
  ctx.rect(canvas.width/2 - (squareSide / 2), canvas.height /2 - (squareSide / 2), squareSide, squareSide);
  ctx.fillStyle = "#0095DD";
  ctx.fill();
  ctx.closePath();

}

  const cRef = useRef(null)
  return (
    <div>
        <canvas  style={{ background: "#eee", display: "block", margin: "0 auto" }} id="myCanvas" width="480" height="320" ref={cRef}></canvas>
    </div>
  );
}

export default App;
