import React, { useState, useEffect, useRef } from 'react';
import EventListener, {withOptions} from 'react-event-listener';

const genRandomXY = () => {
const y = Math.floor(Math.random() * 15)
const x = Math.floor(Math.random() * 23)
return [x * 20, y * 20]
}




class Snake {
  constructor(x, y, direction) {
    this.x = x
    this.y = y
    this.direction = direction
    this.snakeBody = {0:[this.x , this.y], 1:[this.x - 20, this.y], 2:[this.x - 40, this.y], 3:[this.x - 60, this.y]}
    this.debugger = []
    this.pause = false
  }



  changeDirection(direction){
    this.direction = direction
  }

  getTail(){
    return this.snakeBody[this.hasBody() - 1]
  }

  go() {
    this.pause = false
  }

  updatePostion(appleX, appleY){

    if (!this.pause){

    let tail = this.getTail()


    if (this.hasBody() > 1){

      for (let i = this.hasBody() - 1; i >= 1; i--){
        this.snakeBody[i] = this.snakeBody[i - 1]
      }
  }


    if (this.direction == 'up'){
      this.y -= 20
    }
    else if (this.direction == 'down'){
      this.y += 20
    }
    else if (this.direction == 'left'){
      this.x -= 20
    }
    else if (this.direction == 'right'){
      this.x += 20
    }

    this.snakeBody[0] = [this.x, this.y]

    if (this.x === appleX && this.y === appleY){
      this.debugger.push(tail)
    }

    }
  }

  add(){
    if (this.debugger.length == 1){
      this.snakeBody[this.hasBody()] = this.debugger.pop()
    }
  }


  x(){
    return this.x
  }

  y(){
    return this.y
  }

  hasBody() {
    return Object.keys(this.snakeBody).length
  }

  collided() {

      for (let i = 1; i < this.hasBody(); i++){
          let temp_x = this.snakeBody[i][0]
          let temp_y = this.snakeBody[i][1]
          if (this.x === temp_x && this.y === temp_y){
            return true
          }
      }
      return false
  }

  p(){
    this.pause = true
  }

  getSnakeCoords(){
    return Object.values(this.snakeBody)
  }


}

class Apple {

  constructor() {
    let randCoords = genRandomXY()
    this.x = randCoords[0]
    this.y = randCoords[1]
  }

  collision(snakeX, snakeY) {
    if (snakeX === this.x && snakeY === this.y){
      return true
    }
    else{
      return false
    }
  }

  redraw(snakeX, snakeY){
    if (this.collision(snakeX, snakeY)){
      let randCoords = genRandomXY()
      this.x = randCoords[0]
      this.y = randCoords[1]
    }
  }

  getX(){
    return this.x
  }

  getY(){
    return this.y
  }

}

const snake = new Snake(100, 100, 'right')
const apple = new Apple()
const Position = ({stateX, stateY}) => {

  return(
    <div style={{marginLeft: "100px"}}> {stateX} {stateY} </div>
  )
}




const Game = React.memo(({setStateX, setStateY}) => {

  let dx = 0
  let dy = 0

  let appleExists = false

  const squares = new Set()
  for (let i = 0; i < 16; i++){
    for (let j = 0; j < 24; j++){
      squares.add([i*20, j*20])
    }
  }




  let cW
  let cH

  const gameLogic = (ctx, canvas) => {

    if (!snake.collided()){
      drawSnake(ctx, canvas)
      drawApple(ctx, canvas)
      let x = apple.getX()
      let y = apple.getY()
      snake.updatePostion(x, y)
      snake.add()
      apple.redraw(snake.x, snake.y)
    }
  }

  useEffect(() => {


      const canvas = cRef.current
      const ctx = canvas.getContext("2d");
      cW = canvas.width/2
      cH = canvas.height/2
      squares[cW][cH] = true

      // let coords = genRandomXY()

      setStateX(cW)
      setStateY(cH)
      setInterval(() => {gameLogic(ctx, canvas)}, 150)
      window.addEventListener('keydown', logKey, cW, cH);
      return () => {
      window.removeEventListener('keydown', logKey, cW, cH);
  }
  })

  const logKey = (event) => {

    if (event.code === "ArrowRight"){
      snake.changeDirection("right")
    }
    else if (event.code === "ArrowLeft"){
      snake.changeDirection("left")
    }
    else if (event.code === "ArrowUp" ){
      snake.changeDirection("up")
    }
    else if (event.code === "ArrowDown"){
      snake.changeDirection("down")
    }
    else if (event.keyCode === 32){
      snake.p()
    }
    else if (event.keyCode === 13){
      snake.go()
    }

  }

  function drawSnake(ctx, canvas) {


    const squareSide = 20
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.beginPath()
    ctx.rect(snake.x, snake.y, squareSide, squareSide);
    if (snake.hasBody() > 0){

      for (let i = 0; i <= snake.hasBody() - 1; i++){
        try {
            ctx.rect(snake.snakeBody[i][0], snake.snakeBody[i][1], squareSide, squareSide);
        } catch (e) {
          //pass
        } finally {
          //pass
        }

      }
    }
    ctx.fillStyle = "#388E3C";
    ctx.fill();
    ctx.closePath();

}

  function drawApple(ctx, canvas, x, y) {


      const appleRadius = 10
      ctx.beginPath()
      ctx.arc(apple.getX() + appleRadius, apple.getY() + appleRadius, appleRadius, 0, Math.PI*2);
      ctx.fillStyle = "#D32F2F";
      ctx.fill();
      ctx.closePath();


    }




  const cRef = useRef(null)
  return(
    <div>
        <canvas  style={{ background: "#eee", display: "block", margin: "0 auto" }} id="myCanvas" width="480" height="320" ref={cRef}></canvas>
    </div>
  )
})


function App() {

  const [stateX, setStateX] = useState(240)
  const [stateY, setStateY] = useState(160)

  return (
    <div>
        <Game  setStateX={setStateX} setStateY={setStateY}/>
        <Position stateX={stateX} stateY={stateY}/>
    </div>
  );
}

export default App;
