import React, { useState, useEffect, useRef } from 'react';
import EventListener, {withOptions} from 'react-event-listener';


const Position = ({stateX, stateY}) => {

  return(
    <div style={{marginLeft: "100px"}}> {stateX} {stateY} </div>
  )
}

const Game = React.memo(({setStateX, setStateY}) => {

  let dx = 0
  let dy = 0
  let cW
  let cH

  const squares = []
  for (let i = 0; i < 16; i++){
    squares.push([])
    for (let j = 0; j < 24; j++){
      squares[i].push([i * 20, j * 20, true])
    }
  }

  useEffect(() => {


      const canvas = cRef.current
      const ctx = canvas.getContext("2d");
      cW = canvas.width/2
      cH = canvas.height/2
      setStateX(cW)
      setStateY(cH)
      setInterval(() => {draw(ctx, canvas); drawApple(ctx, canvas)}, 10)
      window.addEventListener('keydown', logKey, cW, cH);
      return () => {
      window.removeEventListener('keydown', logKey, cW, cH);
  }
  })

  const logKey = (event) => {

    if (event.code === "ArrowRight" && (cW + dx + 20) <= 460){
      console.log(cW + dx + 20)
      dx += 20

    }
    else if (event.code === "ArrowLeft" && (cW + dx - 20) >= 0){
      console.log(cW + dx - 20)
      dx -= 20
    }
    else if (event.code === "ArrowUp" && (cH + dy - 20) >= 0){
      dy -= 20
    }
    else if (event.code === "ArrowDown" && (cH + dy + 20) <= 300){
      dy += 20
    }
    setStateX(cW + dx)
    setStateY(cH + dy)

  }

  function draw(ctx, canvas) {

    const squareSide = 20
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.beginPath()
    ctx.rect(canvas.width/2 + dx, canvas.height /2  + dy, squareSide, squareSide);
    ctx.fillStyle = "#388E3C";
    ctx.fill();
    ctx.closePath();

}

  function drawApple(ctx, canvas) {

    const appleRadius= 10
    ctx.beginPath()
    ctx.arc(110, 110, appleRadius, 0, Math.PI*2);
    ctx.fillStyle = "#D32F2F";
    ctx.fill();
    ctx.closePath();

}

  const cRef = useRef(null)
  return(
    <div>
        <canvas  style={{ background: "#eee", display: "block", margin: "0 auto" }} id="myCanvas" width="480" height="320" ref={cRef}></canvas>
    </div>
  )
})


function App() {

  const [stateX, setStateX] = useState(240)
  const [stateY, setStateY] = useState(160)

  return (
    <div>
        <Game  setStateX={setStateX} setStateY={setStateY}/>
        <Position stateX={stateX} stateY={stateY}/>
    </div>
  );
}

export default App;
