import React, { useState, useEffect, useRef } from 'react';

const APPLERADIUS = 10

class GameContainer {

  constructor(width, height){
    this.width = width
    this.height = height
    this.wallCollision = false
  }

  update(snake, apple){
    // Update the snake, did it eat the apple or not
    (snake.head.x === apple.x && snake.head.y === apple.y)? snake.update(true): snake.update(false)
    // Update the apple, was it eaten or not
    if (snake.head.x === apple.x && snake.head.y === apple.y){
      // non random regeneration of apple for now
       apple.x = 50
       apple.y = 50
    }
    // check of snake collided with wall
    this.wallCollision = (snake.head.x > this.width || snake.head.x <= 0 || snake.head.y >= this.height || snake.head.y <= 0)?  true: false
  }

}

class Snake {

  constructor()
  {
    this.segmentLocations = [{x: 35, y: 50}, {x: 30, y: 50}]
    this.direction = "right"
    this.head = this.segmentLocations[0]
  }

  update(addSegment){

    console.log("snake update")
    const snakeLength = this.segmentLocations.length
    let currentHead = this.segmentLocations[0]
    let currentTail = this.segmentLocations[snakeLength - 1]

    switch (this.direction) {
      case "right":
        currentHead.x += 5
        break
      case "left":
        currentHead.x -= 5
        break
      case "down":
        currentHead.y += 5
        break
      case "up":
          currentHead.y -= 5
          break
      default:
        console.log("Error: Needs Direction")
    }
    this.segmentLocations.pop()
    this.segmentLocations = [currentHead, ...this.segmentLocations]

    if (addSegment){
      this.segmentLocations.push(currentTail)
    }
  }

  changeDirection(direction){
    this.direction = direction
  }

}

class Apple {

  constructor(location)
  {
    this.x = location.x
    this.y = location.y
  }
}


const Game = () => {

  const gameContainer = new GameContainer()
  const snake = new Snake()
  const apple = new Apple({x:50, y:50})

  const gameLogic = (gameContainer, snake, apple, ctx) => {
    ctx.clearRect(0, 0, gameContainer.x, gameContainer.y);
    ctx.fillStyle = 'black';
    ctx.fillRect(0, 0, gameContainer.x, gameContainer.y)
    ctx.fillStyle = 'green';
    snake.segmentLocations.forEach((segment, index) => {
      ctx.fillRect(segment.x, segment.y, 5, 5);
    })
    ctx.fillStyle = 'red';
    ctx.fillRect(apple.x, apple.y, 5, 5)
    gameContainer.update(snake, apple)

}

  const canvasRef = useRef(null)

  const controller = (event) => {

    switch (event.code ) {
      case "ArrowRight":
        snake.changeDirection("right")
      case "ArrowLeft":
        snake.changeDirection("left")
      case "ArrowUp":
        snake.changeDirection("up")
      case "ArrowDown":
        snake.changeDirection("down")
      default:
        break;
    }
    switch (event.keyCode ) {
      case 32:
        snake.pauseSnake()
      case 13:
        snake.go()
      default:
        break;
    }

  }



  useEffect(() => {


      const canvas = canvasRef.current
      const ctx = canvas.getContext("2d");
      const canvasWidth = canvas.width/2
      const canvasHeight = canvas.height/2

      let gameState = setInterval(() => {gameLogic(gameContainer, snake, apple, ctx)}, 150)
      window.addEventListener('keydown', controller);

      return () => {
      clearInterval(gameState)
      window.removeEventListener('keydown', controller);
      }
  })


  return(
    <div>
        <h1 style ={{fontFamily: "'Press Start 2P', cursive", textAlign: "center"}}> Snake</h1>
        <canvas
          style={{
          background: "#262626",
          display: "block",
          margin: "0 auto" }}
          id="myCanvas"
          width="480"
          height="320"
          ref={canvasRef}></canvas>
    </div>
  )
  }

function App() {

  return(
    <>
    <Game/>
    </>
  )


}

export default App;
