import React, { useState, useEffect, useRef } from 'react';



class Snake {

  constructor(x, y, direction) {
    this.x = x
    this.y = y
    this.direction = direction
    this.snakeBody = {0:[this.x , this.y], 1:[this.x - 20, this.y], 2:[this.x - 40, this.y], 3:[this.x - 60, this.y]}
    this.debugger = []
    this.pause = false
  }

  changeDirection(direction){
    this.direction = direction
  }

  getTail(){
    return this.snakeBody[this.snakeLength() - 1]
  }

  go() {
    this.pause = false
  }

  updatePostion(appleX, appleY){

    if (!this.pause){
      const tail = this.getTail()
      if (this.snakeLength() > 1){

      for (let i = this.snakeLength() - 1; i >= 1; i--){
        this.snakeBody[i] = this.snakeBody[i - 1]
      }
  }

    if (this.direction === 'up'){
      this.y -= 20
    }
    else if (this.direction === 'down'){
      this.y += 20
    }
    else if (this.direction === 'left'){
      this.x -= 20
    }
    else if (this.direction === 'right'){
      this.x += 20
    }

    this.snakeBody[0] = [this.x, this.y]

    if (this.x === appleX && this.y === appleY){
      this.debugger.push(tail)
    }}
  }

  add(){
    if (this.debugger.length === 1){
      this.snakeBody[this.snakeLength()] = this.debugger.pop()
    }
  }

  snakeLength() {
    return Object.keys(this.snakeBody).length
  }

  collided() {

      for (let i = 1; i < this.snakeLength(); i++){
          const temp_x = this.snakeBody[i][0]
          const temp_y = this.snakeBody[i][1]
          if (this.x === temp_x && this.y === temp_y){
            return true
          }
      }
      if (this.y > 300 || this.y < 0 || this.x < 0 || this.x > 460){
        return true

      }
      return false
  }

  pauseSnake(){
    this.pause = true
  }

  getSnakeCoords(){
    return Object.values(this.snakeBody)
  }

  resetSnake() {
      this.x = 100
      this.y = 100
      this.snakeBody = {0:[this.x , this.y], 1:[this.x - 20, this.y], 2:[this.x - 40, this.y], 3:[this.x - 60, this.y]}
  }
}

class Apple {

  constructor(snake) {
    const randCoords = genRandomXY(snake.getSnakeCoords())
    this.x = randCoords[0]
    this.y = randCoords[1]
  }

  collision(snakeX, snakeY, setScore, localScore) {
    if (snakeX === this.x && snakeY === this.y){
      localScore += 1
      setScore(localScore)
      return true
    }
    else{
      return false
    }
  }

  redraw(snakeX, snakeY, setScore, localScore, snake){
    if (this.collision(snakeX, snakeY, setScore, localScore)){
      const randCoords = genRandomXY(snake.getSnakeCoords())
      this.x = randCoords[0]
      this.y = randCoords[1]
    }
  }

  getX(){
    return this.x
  }

  getY(){
    return this.y
  }
}


const genRandomXY = (snakeBody) => {

  const [x, y] = [Math.floor(Math.random() * 15) * 20, Math.floor(Math.random() * 23) * 20]
  let tryAgain = false
  for (let i = 0; i < snakeBody.length; i++){
    const temp_x = snakeBody[i][0]
    const temp_y = snakeBody[i][1]
    tryAgain = (x === temp_x && y === temp_y)&& true

  }
  return tryAgain? genRandomXY(snakeBody): [x, y]

}



const Game = React.memo(({setStateX, setStateY, setRestart, x, setX, setScore}) => {


  let localScore = 0
  const snake = new Snake(100, 100, 'right')
  const apple = new Apple(snake)
  const canvasRef = useRef(null)

  const controller = (event) => {

    if (event.code === "ArrowRight"){
      snake.changeDirection("right")
    }
    else if (event.code === "ArrowLeft"){
      snake.changeDirection("left")
    }
    else if (event.code === "ArrowUp" ){
      snake.changeDirection("up")
    }
    else if (event.code === "ArrowDown"){
      snake.changeDirection("down")
    }
    else if (event.keyCode === 32){
      snake.pauseSnake()
    }
    else if (event.keyCode === 13){
      snake.go()
    }
  }

  useEffect(() => {


      const canvas = canvasRef.current
      const ctx = canvas.getContext("2d");
      const canvasWidth = canvas.width/2
      const canvasHeight = canvas.height/2

      const gameLogic = (ctx, canvas) => {

        let x_ = snake.x
        let y_ = snake.y
        console.log(x_, y_)
        const snakeCollided = snake.collided()

        if (!snakeCollided){
          drawSnake(ctx, canvas)
          drawApple(ctx, canvas)
          const x = apple.getX()
          const y = apple.getY()
          snake.updatePostion(x, y)
          snake.add()
          apple.redraw(snake.x, snake.y, setScore, localScore, snake)
          setStateX(snake.x)
          setStateY(snake.y)

        }
        else if (snakeCollided){
            setRestart(true)
        }
      }

      let gameState = setInterval(() => {gameLogic(ctx, canvas)}, 150)

      if (x){
        snake.resetSnake()
        setX(false)
        localScore = 0
        setScore(localScore)
      }
      window.addEventListener('keydown', controller, canvasWidth, canvasHeight);
      return () => {
      clearInterval(gameState)
      window.removeEventListener('keydown', controller, canvasWidth, canvasHeight);
  }
},[x])



  function drawSnake(ctx, canvas) {

    const squareSide = 20
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.beginPath()
    ctx.rect(snake.x, snake.y, squareSide, squareSide);
    if (snake.snakeLength() > 0){

      for (let i = 0; i <= snake.snakeLength() - 1; i++){

          ctx.rect(snake.snakeBody[i][0], snake.snakeBody[i][1], squareSide, squareSide);
      }
    }
    ctx.fillStyle = "#388E3C";
    ctx.fill();
    ctx.closePath();
}

  function drawApple(ctx, canvas, x, y) {


      const appleRadius = 10
      ctx.beginPath()
      ctx.arc(apple.getX() + appleRadius, apple.getY() + appleRadius, appleRadius, 0, Math.PI*2);
      ctx.fillStyle = "#D32F2F";
      ctx.fill();
      ctx.closePath();
    }



  return(
    <div>
        <h1 style ={{fontFamily: "'Press Start 2P', cursive", textAlign: "center"}}> Snake</h1>
        <canvas  style={{ background: "#262626", display: "block", margin: "0 auto" }} id="myCanvas" width="480" height="320" ref={canvasRef}></canvas>
    </div>
  )
})

function App() {

  const [stateX, setStateX] = useState(240)
  const [stateY, setStateY] = useState(160)
  const [restart, setRestart] = useState(false)
  const [x, setX] = useState(false)
  const [score, updateScore] = useState(0)


  return (
    <div>
        <Game setScore = {updateScore} setX ={setX} x={x} setRestart ={setRestart} setStateX={setStateX} setStateY={setStateY}/>


        {restart &&   <h4 onClick={() => {setRestart(false); setX(true)}} style ={{fontFamily: "'Press Start 2P', cursive", textAlign: "center", cursor:"grab"}}> New Game</h4>}
        <h4 style ={{fontFamily: "'Press Start 2P', cursive", textAlign: "center", cursor:"grab"}}>Score: {score}</h4>
    </div>
  );
}

export default App;
