import React, { useState, useEffect, useRef } from 'react';
import EventListener, {withOptions} from 'react-event-listener';



const Position = ({stateX, stateY}) => {

  return(
    <div style={{marginLeft: "100px"}}> {stateX} {stateY} </div>
  )
}




const Game = React.memo(({setStateX, setStateY, setRestart, x}) => {


  const [newGame, setNewGame] = useState(false)

  const genRandomXY = (snakeBody) => {

      const y = Math.floor(Math.random() * 15) * 20
      const x = Math.floor(Math.random() * 23) * 20
      let recurse = false
      for (let i = 0; i < snakeBody.length; i++){
        let temp_x = snakeBody[i][0]
        let temp_y = snakeBody[i][1]
        if (x === temp_x && y === temp_y){
          recurse = true
        }
      }
      if (recurse){
        return genRandomXY(snakeBody)
      }
      else{
        return [x, y]
      }
  }

  class Snake {

    constructor(x, y, direction) {
      this.x = x
      this.y = y
      this.direction = direction
      this.snakeBody = {0:[this.x , this.y], 1:[this.x - 20, this.y], 2:[this.x - 40, this.y], 3:[this.x - 60, this.y]}
      this.debugger = []
      this.pause = false
    }

    changeDirection(direction){
      this.direction = direction
    }

    getTail(){
      return this.snakeBody[this.snakeLength() - 1]
    }

    go() {
      this.pause = false
    }

    updatePostion(appleX, appleY){

      if (!this.pause){
        let tail = this.getTail()
        if (this.snakeLength() > 1){

        for (let i = this.snakeLength() - 1; i >= 1; i--){
          this.snakeBody[i] = this.snakeBody[i - 1]
        }
    }


      if (this.direction == 'up'){
        this.y -= 20
      }
      else if (this.direction == 'down'){
        this.y += 20
      }
      else if (this.direction == 'left'){
        this.x -= 20
      }
      else if (this.direction == 'right'){
        this.x += 20
      }

      this.snakeBody[0] = [this.x, this.y]

      if (this.x === appleX && this.y === appleY){
        this.debugger.push(tail)
      }}
    }

    add(){
      if (this.debugger.length == 1){
        this.snakeBody[this.snakeLength()] = this.debugger.pop()
      }
    }


    snakeLength() {
      return Object.keys(this.snakeBody).length
    }

    collided() {

        for (let i = 1; i < this.snakeLength(); i++){
            let temp_x = this.snakeBody[i][0]
            let temp_y = this.snakeBody[i][1]
            if (this.x === temp_x && this.y === temp_y){
              return true
            }
        }
        if (this.y > 300 || this.y < 0 || this.x < 0 || this.x > 460){
          return true
        }
        return false
    }

    p(){
      this.pause = true
    }

    getSnakeCoords(){
      return Object.values(this.snakeBody)
    }


  }

  class Apple {

    constructor() {
      let randCoords = genRandomXY(snake.getSnakeCoords())
      this.x = randCoords[0]
      this.y = randCoords[1]
    }

    collision(snakeX, snakeY) {
      if (snakeX === this.x && snakeY === this.y){
        return true
      }
      else{
        return false
      }
    }

    redraw(snakeX, snakeY){
      if (this.collision(snakeX, snakeY)){
        let randCoords = genRandomXY(snake.getSnakeCoords())
        this.x = randCoords[0]
        this.y = randCoords[1]
      }
    }

    getX(){
      return this.x
    }

    getY(){
      return this.y
    }

  }



  let cW
  let cH
  let snake = new Snake(100, 100, 'right')
  let apple = new Apple()

  const gameLogic = (ctx, canvas) => {

    let c = snake.collided()
    console.log(c)
    if (!c){
      drawSnake(ctx, canvas)
      drawApple(ctx, canvas)
      let x = apple.getX()
      let y = apple.getY()
      snake.updatePostion(x, y)
      snake.add()
      apple.redraw(snake.x, snake.y)
      setStateX(snake.x)
      setStateY(snake.y)

    }
    else if (c){
        setRestart(true)



    }




  }

  useEffect(() => {

      console.log('fired')
      const canvas = cRef.current
      const ctx = canvas.getContext("2d");
      cW = canvas.width/2
      cH = canvas.height/2

      setInterval(() => {gameLogic(ctx, canvas)}, 150)
      window.addEventListener('keydown', logKey, cW, cH);
      return () => {
      window.removeEventListener('keydown', logKey, cW, cH);
  }
},[x])

  const logKey = (event) => {

    if (event.code === "ArrowRight"){
      snake.changeDirection("right")
    }
    else if (event.code === "ArrowLeft"){
      snake.changeDirection("left")
    }
    else if (event.code === "ArrowUp" ){
      snake.changeDirection("up")
    }
    else if (event.code === "ArrowDown"){
      snake.changeDirection("down")
    }
    else if (event.keyCode === 32){
      snake.p()
    }
    else if (event.keyCode === 13){
      snake.go()
    }

  }

  function drawSnake(ctx, canvas) {


    const squareSide = 20
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.beginPath()
    ctx.rect(snake.x, snake.y, squareSide, squareSide);
    if (snake.snakeLength() > 0){

      for (let i = 0; i <= snake.snakeLength() - 1; i++){

          ctx.rect(snake.snakeBody[i][0], snake.snakeBody[i][1], squareSide, squareSide);


      }
    }
    ctx.fillStyle = "#388E3C";
    ctx.fill();
    ctx.closePath();

}

  function drawApple(ctx, canvas, x, y) {


      const appleRadius = 10
      ctx.beginPath()
      ctx.arc(apple.getX() + appleRadius, apple.getY() + appleRadius, appleRadius, 0, Math.PI*2);
      ctx.fillStyle = "#D32F2F";
      ctx.fill();
      ctx.closePath();


    }




  const cRef = useRef(null)

  return(
    <div>
        <h1 style ={{fontFamily: "'Press Start 2P', cursive", textAlign: "center"}}> Snake</h1>
        <canvas  style={{ background: "#262626", display: "block", margin: "0 auto" }} id="myCanvas" width="480" height="320" ref={cRef}></canvas>
    </div>
  )
})


function App() {

  const [stateX, setStateX] = useState(240)
  const [stateY, setStateY] = useState(160)
  const [restart, setRestart] = useState(false)
  const [x, setX] = useState(false)


  return (
    <div>
        <Game x={x} setRestart ={setRestart} setStateX={setStateX} setStateY={setStateY}/>
        <Position stateX={stateX} stateY={stateY}/>
        {restart &&   <h4 onClick={() => {setRestart(false); setX(!x)}} style ={{fontFamily: "'Press Start 2P', cursive", textAlign: "center"}}> New Game</h4>}

    </div>
  );
}

export default App;
